function cut($string, $length) {
    $strlen = strlen($string);

    if($strlen <= $length) {
        return $string;

    }
    $string2 = substr($string, 0, $length);
    $position = strrpos($string2, ' ');
    if($position === false) {
        $position = strpos($string, ' ', 0);
    }
    $string = substr($string, 0, $position);
    return $string . '...';
}
